mapper.select:
select a.*,b.*,c.*
from product a join p_category b on a.p_category_idx = b.p_category_idx join product_img c on a.p_idx=c.p_idx 
where a.p_category_idx=#{p_category_idx}
GROUP by a.p_idx;

select a.*,b.*,c.*
from product a join p_category b on a.p_category_idx = b.p_category_idx join product_img c on a.p_idx=c.p_idx 
where b.p_category_large=#{p_category_large}
GROUP by a.p_idx;

select a.*
from product_img a join product b on a.p_idx=b.p_idx
where b.p_idx=#{p_idx};


select a.*, b.*, MIN(p_front_img), c.p_sys_filename, c.p_idx
from product a join p_category b on a.p_category_idx = b.p_category_idx join product_img c on a.p_idx=c.p_idx 
where a.p_idx=#{p_idx}

select a.p_name, a.p_idx, a.m_nick, MIN(p_front_img), c.p_sys_filename
from product a join product_img c on a.p_idx=c.p_idx join sellerStory b on a.seller_idx=b.seller_idx
where a.p_idx=#{p_idx}

select a.*, b.p_name
from review a join product b on a.p_idx=b.p_idx join orders c on a.orders_idx=c.orders_idx
where b.p_idx=#{p_idx}

SELECT b.*
from sellerstory a join product b on a.seller_idx=b.seller_idx
where b.seller_idx=#{seller_idx};
