mapper.select:
select seller_idx
from members a join sellerstory b on a.midx=b.midx
where a.midx=#{member_id}

select a.seller_idx, a.p_idx, p_name, b.detail_idx, detail_status, fixprice, orders_day,c.orders_idx, c.midx, d.m_name, d.m_id from product a join detail b on a.p_idx=b.p_idx join orders c on b.orders_idx=c.orders_idx join members d on c.midx=d.midx join detail_day e on b.detail_idx=e.detail_idx where a.seller_idx = (select midx from sellerstory where midx=#{member_id})
and (d.m_name like CONCAT('%',#{keyword},'%') or d.m_id like CONCAT('%',#{keyword},'%'))
and d.m_name like CONCAT('%',#{keyword},'%')
and d.m_id like CONCAT('%',#{keyword},'%')
and detail_status=#{status}
and a.p_idx=#{product}
